import React, { useRef, useEffect, useState } from 'react';
import styled, { keyframes } from 'styled-components';
import Webcam from 'react-webcam';
import io from 'socket.io-client';
import config from '../config/config';

// Keyframe animations
const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
    opacity: 1;
  }
  50% {
    transform: scale(1.05);
    opacity: 0.8;
  }
`;

const borderFlow = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -1000px 0;
  }
  100% {
    background-position: 1000px 0;
  }
`;

const bounce = keyframes`
  0%, 20%, 50%, 80%, 100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  60% {
    transform: translateY(-5px);
  }
`;

const glow = keyframes`
  0%, 100% {
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.3);
  }
  50% {
    box-shadow: 0 0 40px rgba(255, 215, 0, 0.8);
  }
`;

const ripple = keyframes`
  0% {
    transform: scale(0);
    opacity: 1;
  }
  100% {
    transform: scale(4);
    opacity: 0;
  }
`;

// Styled Components
const WebcamContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin: 2rem 0;
  position: relative;
`;

const VideoContainer = styled.div`
  position: relative;
  border-radius: 20px;
  overflow: hidden;
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  padding: 4px;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-radius: 20px;
    background: ${props => props.isAnalyzing 
      ? 'linear-gradient(45deg, #FFD700, #FFA500, #FF6B6B, #4CAF50, #667eea, #FFD700)'
      : 'linear-gradient(45deg, rgba(255, 255, 255, 0.3), rgba(255, 255, 255, 0.1))'
    };
    background-size: 400% 400%;
    animation: ${props => props.isAnalyzing ? borderFlow : 'none'} 3s ease infinite;
    z-index: -1;
  }
  
  box-shadow: ${props => props.isAnalyzing 
    ? '0 20px 60px rgba(255, 215, 0, 0.4)'
    : '0 15px 40px rgba(0, 0, 0, 0.3)'
  };
  
  transition: all 0.3s ease;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 25px 80px rgba(0, 0, 0, 0.4);
  }
`;

const WebcamElement = styled(Webcam)`
  width: 640px;
  height: 480px;
  object-fit: cover;
  border-radius: 16px;
  position: relative;
  z-index: 1;
  
  @media (max-width: 768px) {
    width: 100%;
    max-width: 480px;
    height: auto;
  }
`;

const OverlayIndicators = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  right: 20px;
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  z-index: 2;
  pointer-events: none;
`;

const LiveIndicator = styled.div`
  background: ${props => props.isLive ? '#FF4444' : 'rgba(0, 0, 0, 0.6)'};
  color: white;
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  backdrop-filter: blur(10px);
  
  .live-dot {
    width: 8px;
    height: 8px;
    background: white;
    border-radius: 50%;
    animation: ${props => props.isLive ? pulse : 'none'} 1s infinite;
  }
`;

const QualityIndicator = styled.div`
  background: rgba(0, 0, 0, 0.6);
  color: ${props => {
    if (props.quality === 'excellent') return '#4CAF50';
    if (props.quality === 'good') return '#FFD700';
    return '#FF6B6B';
  }};
  padding: 0.5rem 1rem;
  border-radius: 20px;
  font-size: 0.9rem;
  font-weight: bold;
  backdrop-filter: blur(10px);
`;

const ControlsContainer = styled.div`
  display: flex;
  gap: 1.5rem;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
`;

const ControlButton = styled.button`
  position: relative;
  padding: 1.2rem 2.5rem;
  border: none;
  border-radius: 50px;
  background: ${props => {
    if (props.variant === 'start') return 'linear-gradient(45deg, #4CAF50, #45a049)';
    if (props.variant === 'stop') return 'linear-gradient(45deg, #f44336, #d32f2f)';
    return 'linear-gradient(45deg, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0.1))';
  }};
  color: white;
  font-family: 'Poppins', sans-serif;
  font-weight: 600;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
    transition: left 0.6s ease;
  }
  
  &:hover::before {
    left: 100%;
  }
  
  &:hover {
    transform: translateY(-3px) scale(1.05);
    box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  }
  
  &:active {
    transform: translateY(-1px) scale(1.02);
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
    
    &:hover {
      transform: none;
      box-shadow: none;
    }
  }
  
  .ripple {
    position: absolute;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.6);
    transform: scale(0);
    animation: ${ripple} 0.6s linear;
    pointer-events: none;
