import React from 'react';
import styled from 'styled-components';

const AboutContainer = styled.div`
  padding: 2rem;
  text-align: center;
  background: #6D71D0;
  color: white;
`;

const Title = styled.h2`
  font-size: 2rem;
  margin-bottom: 1rem;
`;

const Description = styled.p`
  font-size: 1.2rem;
  line-height: 1.6;
  max-width: 800px;
  margin: 0 auto;
`;

const AboutSection = () => {
  return (
    <AboutContainer>
      <Title>About Posture Detection</Title>
      <Description>
        Our app uses advanced AI and posture analysis techniques to help you improve your health and well-being. Whether you're working at a desk or performing squats, our app provides real-time feedback and actionable insights.
      </Description>
    </AboutContainer>
  );
};

export default AboutSection;
