import React from 'react';
import styled from 'styled-components';

const AnalysisContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 2rem;
  margin: 2rem 0;
  backdrop-filter: blur(10px);
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const AnalysisHeader = styled.div`
  text-align: center;
  margin-bottom: 2rem;
  
  h2 {
    font-size: 1.8rem;
    margin-bottom: 1rem;
    color: #FFD700;
  }
  
  .analysis-type {
    background: rgba(255, 215, 0, 0.2);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    display: inline-block;
    font-weight: bold;
  }
`;

const ScoreContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 2rem 0;
`;

const ScoreCircle = styled.div`
  width: 120px;
  height: 120px;
  border-radius: 50%;
  background: conic-gradient(
    ${props => {
      const score = props.score || 0;
      if (score >= 80) return '#4CAF50 0deg, #4CAF50 ' + (score * 3.6) + 'deg, rgba(255,255,255,0.2) ' + (score * 3.6) + 'deg';
      if (score >= 60) return '#FFD700 0deg, #FFD700 ' + (score * 3.6) + 'deg, rgba(255,255,255,0.2) ' + (score * 3.6) + 'deg';
      return '#ff6b6b 0deg, #ff6b6b ' + (score * 3.6) + 'deg, rgba(255,255,255,0.2) ' + (score * 3.6) + 'deg';
    }}
  );
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  
  &::before {
    content: '';
    position: absolute;
    width: 90px;
    height: 90px;
    background: inherit;
    border-radius: 50%;
    backdrop-filter: blur(10px);
  }
  
  .score-text {
    position: relative;
    z-index: 1;
    font-size: 1.5rem;
    font-weight: bold;
    color: white;
  }
`;

const SummaryGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin: 2rem 0;
`;

const SummaryCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  padding: 1.5rem;
  text-align: center;
  
  .card-icon {
    font-size: 2rem;
    margin-bottom: 0.5rem;
  }
  
  .card-title {
    font-weight: bold;
    margin-bottom: 0.5rem;
    color: #E0E0E0;
  }
  
  .card-value {
    font-size: 1.5rem;
    font-weight: bold;
    color: #FFD700;
  }
`;

const FeedbackSection = styled.div`
  margin: 2rem 0;
  
  h3 {
    margin-bottom: 1rem;
    color: #FFD700;
  }
`;

const FeedbackList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
`;

const FeedbackItem = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  border-left: 4px solid ${props => props.isGood ? '#4CAF50' : '#ff6b6b'};
  
  .feedback-icon {
    font-size: 1.2rem;
    margin-top: 0.1rem;
  }
  
  .feedback-content {
    flex: 1;
    
    .feedback-type {
      font-weight: bold;
      margin-bottom: 0.25rem;
      color: ${props => props.isGood ? '#4CAF50' : '#ff6b6b'};
    }
    
    .feedback-text {
      opacity: 0.9;
      line-height: 1.4;
    }
  }
`;

const VideoSummary = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 10px;
  padding: 1.5rem;
  margin: 1.5rem 0;
  
  h4 {
    margin-bottom: 1rem;
    color: #FFD700;
  }
  
  .summary-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 1rem;
    margin-bottom: 1rem;
  }
  
  .stat-item {
    text-align: center;
    
    .stat-label {
      font-size: 0.9rem;
      opacity: 0.7;
      margin-bottom: 0.25rem;
    }
    
    .stat-value {
      font-size: 1.2rem;
      font-weight: bold;
      color: #FFD700;
    }
  }
`;

const IssueTypesList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const IssueTag = styled.span`
  background: rgba(255, 107, 107, 0.2);
  color: #ff6b6b;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.9rem;
  border: 1px solid rgba(255, 107, 107, 0.3);
`;

const PostureAnalysis = ({ results }) => {
  if (!results) return null;

  const isVideoAnalysis = results.results && results.results.summary;
  const analysisData = isVideoAnalysis ? results.results : results;
  
  const getScoreColor = (score) => {
    if (score >= 80) return '#4CAF50';
    if (score >= 60) return '#FFD700';
    return '#ff6b6b';
  };

  const getScoreRating = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Fair';
    return 'Needs Improvement';
  };

  const formatIssueType = (issueType) => {
    return issueType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  if (isVideoAnalysis) {
    const { summary, frames } = analysisData;
    
    return (
      <AnalysisContainer className="fade-in">
        <AnalysisHeader>
          <h2>üìä Analysis Results</h2>
          <div className="analysis-type">
            {results.analysisType === 'squat' ? 'üèãÔ∏è Squat Analysis' : 'üí∫ Desk Posture Analysis'}
          </div>
        </AnalysisHeader>

        <ScoreContainer>
          <ScoreCircle score={summary.averageScore}>
            <div className="score-text">{summary.averageScore}%</div>
          </ScoreCircle>
        </ScoreContainer>

        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h3 style={{ color: getScoreColor(summary.averageScore) }}>
            {getScoreRating(summary.averageScore)}
          </h3>
          <p style={{ opacity: 0.8 }}>Overall Rating: {summary.overallRating}</p>
        </div>

        <VideoSummary>
          <h4>üìπ Video Analysis Summary</h4>
          <div className="summary-stats">
            <div className="stat-item">
              <div className="stat-label">Frames Analyzed</div>
              <div className="stat-value">{summary.frameCount || frames.length}</div>
            </div>
            <div className="stat-item">
              <div className="stat-label">Total Issues</div>
              <div className="stat-value">{summary.totalIssues}</div>
            </div>
            <div className="stat-item">
              <div className="stat-label">Avg Score</div>
              <div className="stat-value">{summary.averageScore}%</div>
            </div>
          </div>
          
          {summary.issueTypes && summary.issueTypes.length > 0 && (
            <>
              <div style={{ marginTop: '1rem', marginBottom: '0.5rem', fontWeight: 'bold' }}>
                Common Issues Detected:
              </div>
              <IssueTypesList>
                {summary.issueTypes.map((issueType, index) => (
                  <IssueTag key={index}>
                    {formatIssueType(issueType)}
                  </IssueTag>
                ))}
              </IssueTypesList>
            </>
          )}
        </VideoSummary>

        <SummaryGrid>
          <SummaryCard>
            <div className="card-icon">üéØ</div>
            <div className="card-title">Accuracy</div>
            <div className="card-value">{summary.averageScore}%</div>
          </SummaryCard>
          <SummaryCard>
            <div className="card-icon">‚ö†Ô∏è</div>
            <div className="card-title">Issues Found</div>
            <div className="card-value">{summary.totalIssues}</div>
          </SummaryCard>
          <SummaryCard>
            <div className="card-icon">‚≠ê</div>
            <div className="card-title">Rating</div>
            <div className="card-value">{summary.overallRating}</div>
          </SummaryCard>
        </SummaryGrid>
      </AnalysisContainer>
    );
  }

  // Real-time analysis display
  const analysis = analysisData.analysis || analysisData;
  
  return (
    <AnalysisContainer className="fade-in">
      <AnalysisHeader>
        <h2>üéØ Real-time Analysis</h2>
        <div className="analysis-type">
          {analysis.postureType === 'squat' ? 'üèãÔ∏è Squat Analysis' : 'üí∫ Desk Posture Analysis'}
        </div>
      </AnalysisHeader>

      <ScoreContainer>
        <ScoreCircle score={analysis.score}>
          <div className="score-text">{analysis.score}%</div>
        </ScoreCircle>
      </ScoreContainer>

      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h3 style={{ color: getScoreColor(analysis.score) }}>
          {getScoreRating(analysis.score)}
        </h3>
      </div>

      <FeedbackSection>
        <h3>üí° Feedback & Recommendations</h3>
        <FeedbackList>
          {analysis.issues && analysis.issues.length > 0 ? (
            analysis.feedback.map((feedback, index) => (
              <FeedbackItem key={index} isGood={false}>
                <div className="feedback-icon">‚ö†Ô∏è</div>
                <div className="feedback-content">
                  <div className="feedback-type">Issue Detected</div>
                  <div className="feedback-text">{feedback}</div>
                </div>
              </FeedbackItem>
            ))
          ) : (
            <FeedbackItem isGood={true}>
              <div className="feedback-icon">‚úÖ</div>
              <div className="feedback-content">
                <div className="feedback-type">Excellent Posture!</div>
                <div className="feedback-text">
                  Your posture looks great! Keep maintaining this good form.
                </div>
              </div>
            </FeedbackItem>
          )}
        </FeedbackList>
      </FeedbackSection>
    </AnalysisContainer>
  );
};

export default PostureAnalysis;
