  background-size: 400% 400%;
  animation: ${gradientShift} 15s ease infinite;
  color: white;
  position: relative;
  overflow-x: hidden;
`;

// Navigation Bar
const NavBar = styled.nav`
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(26, 26, 46, 0.95);
  backdrop-filter: blur(20px);
  padding: 1rem 0;
  z-index: 1000;
  border-bottom: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;

  &.scrolled {
    background: rgba(26, 26, 46, 0.98);
    box-shadow: 0 2px 20px rgba(0, 0, 0, 0.3);
  }
`;

const NavContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 0 2rem;

  @media (max-width: 768px) {
    padding: 0 1rem;
  }
`;

const Logo = styled.div`
  font-size: 1.5rem;
  font-weight: bold;
  background: linear-gradient(45deg, #667eea, #764ba2);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const NavLinks = styled.div`
  display: flex;
  gap: 2rem;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled.a`
  color: rgba(255, 255, 255, 0.8);
  text-decoration: none;
  font-weight: 500;
  transition: all 0.3s ease;
  position: relative;

  &:hover {
    color: #667eea;
    transform: translateY(-2px);
  }

  &::after {
    content: '';
    position: absolute;
    width: 0;
    height: 2px;
    bottom: -5px;
    left: 50%;
    background: linear-gradient(45deg, #667eea, #764ba2);
    transition: all 0.3s ease;
    transform: translateX(-50%);
  }

  &:hover::after {
    width: 100%;
  }
`;

// Professional Section Container
const Section = styled.section`
  padding: 100px 0;
  position: relative;

  &:first-child {
    padding-top: 120px;
  }
`;

// Analysis Section with Professional Gradient
const AnalysisSection = styled(Section)`
  background: linear-gradient(135deg, #0f4c75 0%, #3282b8 50%, #0f4c75 100%);
  background-size: 200% 200%;
  animation: ${gradientShift} 10s ease infinite;
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="grid" width="10" height="10" patternUnits="userSpaceOnUse"><path d="M 10 0 L 0 0 0 10" fill="none" stroke="rgba(255,255,255,0.03)" stroke-width="1"/></pattern></defs><rect width="100" height="100" fill="url(%23grid)"/></svg>');
    opacity: 0.5;
  }
`;

const MainContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 1;

  @media (max-width: 768px) {
    padding: 0 1rem;
  }
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
  background: linear-gradient(45deg, #667eea, #764ba2, #ffeaa7);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: ${fadeInUp} 1s ease-out;

  @media (max-width: 768px) {
    font-size: 2rem;
  }
`;

const ModeSelector = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  margin: 3rem 0;
  animation: ${fadeInUp} 1s ease-out 0.2s both;
`;

const ModeCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 20px;
  padding: 2rem;
  text-align: center;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  cursor: pointer;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -50%;
    width: 200%;
    height: 200%;
    background: ${props => props.active ? 
      'linear-gradient(45deg, rgba(102, 126, 234, 0.3), rgba(118, 75, 162, 0.3))' : 
      'transparent'};
    transition: all 0.3s ease;
    z-index: -1;
  }

  &:hover {
    transform: translateY(-5px) scale(1.02);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
    border-color: rgba(102, 126, 234, 0.5);
  }

  &:hover::before {
    background: linear-gradient(45deg, rgba(102, 126, 234, 0.2), rgba(118, 75, 162, 0.2));
  }
`;

const ModeIcon = styled.div`
  font-size: 3rem;
  margin-bottom: 1rem;
  animation: ${float} 3s ease-in-out infinite;
`;

const ModeTitle = styled.h3`
  font-size: 1.3rem;
  margin-bottom: 0.5rem;
  color: #fff;
`;

const ModeDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

const AnalysisTypeSelector = styled.div`
  display: flex;
  justify-content: center;
  gap: 2rem;
  margin: 3rem 0;
  flex-wrap: wrap;
  animation: ${fadeInUp} 1s ease-out 0.4s both;
`;

const TypeButton = styled.button`
  padding: 1.2rem 2.5rem;
  border: 2px solid ${props => props.active ? '#ffeaa7' : 'rgba(255, 255, 255, 0.3)'};
  border-radius: 50px;
  background: ${props => props.active ? 
    'linear-gradient(45deg, #ffeaa7, #fdcb6e)' : 
    'rgba(255, 255, 255, 0.1)'};
  color: ${props => props.active ? '#2d3436' : 'white'};
  font-weight: bold;
  font-size: 1.1rem;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    width: 0;
    height: 0;
    background: radial-gradient(circle, rgba(255, 234, 167, 0.3) 0%, transparent 70%);
    transition: all 0.3s ease;
    transform: translate(-50%, -50%);
  }

  &:hover {
    border-color: #ffeaa7;
    transform: translateY(-3px);
    box-shadow: 0 10px 25px rgba(255, 234, 167, 0.3);
  }

  &:hover::before {
    width: 300px;
    height: 300px;
  }

  @media (max-width: 768px) {
    padding: 1rem 2rem;
    font-size: 1rem;
  }
`;

function App() {
  const [mode, setMode] = useState('webcam'); // 'webcam' or 'upload'
  const [analysisType, setAnalysisType] = useState('squat'); // 'squat' or 'desk'
  const [analysisResults, setAnalysisResults] = useState(null);

  const handleAnalysisComplete = (results) => {
    setAnalysisResults(results);
  };

  return (
    <AppContainer>
      <Header />
      <MainContent>
        <AnalysisTypeSelector>
          <TypeButton 
            active={analysisType === 'squat'} 
            onClick={() => setAnalysisType('squat')}
          >
            🏋️ Squat Analysis
          </TypeButton>
          <TypeButton 
            active={analysisType === 'desk'} 
            onClick={() => setAnalysisType('desk')}
          >
            💺 Desk Posture
          </TypeButton>
        </AnalysisTypeSelector>

        <ModeSelector>
          <ModeButton 
            active={mode === 'webcam'} 
            onClick={() => setMode('webcam')}
          >
            📹 Live Webcam
          </ModeButton>
          <ModeButton 
            active={mode === 'upload'} 
            onClick={() => setMode('upload')}
          >
            📁 Upload Video
          </ModeButton>
        </ModeSelector>

        {mode === 'webcam' ? (
          <WebcamCapture 
            analysisType={analysisType}
            onAnalysisComplete={handleAnalysisComplete}
          />
        ) : (
          <VideoUpload 
            analysisType={analysisType}
            onAnalysisComplete={handleAnalysisComplete}
          />
        )}

        {analysisResults && (
          <PostureAnalysis results={analysisResults} />
        )}
      </MainContent>
    </AppContainer>
  );
}

export default App;
