import React from 'react';
import styled, { keyframes } from 'styled-components';

const fadeIn = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

const HeroContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 60vh;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const VideoBg = styled.video`
  position: absolute;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: 1;
  filter: brightness(0.7) blur(1px);
  animation: ${fadeIn} 1.5s ease;
`;

const Overlay = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  background: linear-gradient(120deg, #667eea88 0%, #764ba288 100%);
  z-index: 2;
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
  color: #fff;
  text-align: center;
  animation: ${fadeIn} 2s;
`;

const AnimatedHero = () => (
  <HeroContainer>
    <VideoBg autoPlay loop muted playsInline>
      <source src="/your-hero-video.mp4" type="video/mp4" />
      {/* Add fallback image or message */}
    </VideoBg>
    <Overlay />
    <Content>
      <h1>Posture Perfect</h1>
      <p>AI-Powered Posture Analysis for Better Health</p>
    </Content>
  </HeroContainer>
);

export default AnimatedHero;
