import React, { useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';
import gsap from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';

gsap.registerPlugin(ScrollTrigger);

const fadeIn = keyframes`
  from { opacity: 0; }
  to { opacity: 1; }
`;

const HeroContainer = styled.div`
  position: relative;
  width: 100vw;
  height: 100vh;
  overflow: hidden;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, #667eea, #764ba2);
`;

const VideoBg = styled.video`
  position: absolute;
  width: 100%;
  height: 100%;
  object-fit: cover;
  z-index: 1;
  filter: brightness(0.5) blur(2px);
  animation: ${fadeIn} 1.5s ease;
`;

const Overlay = styled.div`
  position: absolute;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.4);
  z-index: 2;
`;

const Content = styled.div`
  position: relative;
  z-index: 3;
  color: #fff;
  text-align: center;
`;

const Title = styled.h1`
  font-size: 3rem;
  font-weight: bold;
  margin-bottom: 1rem;
`;

const Subtitle = styled.p`
  font-size: 1.5rem;
  opacity: 0.9;
`;

const CallToAction = styled.button`
  margin-top: 2rem;
  padding: 1rem 2rem;
  font-size: 1rem;
  font-weight: bold;
  color: #fff;
  background: #4caf50;
  border: none;
  border-radius: 5px;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    background: #45a049;
    transform: scale(1.1);
  }
`;

const AnimatedHero = () => {
  const heroRef = useRef(null);

  useEffect(() => {
    const ctx = gsap.context(() => {
      gsap.fromTo(
        heroRef.current,
        { opacity: 0, y: 50 },
        {
          opacity: 1,
          y: 0,
          duration: 1.5,
          ease: 'power3.out',
          scrollTrigger: {
            trigger: heroRef.current,
            start: 'top center',
            end: 'bottom top',
            scrub: true,
          },
        }
      );
    });

    return () => ctx.revert();
  }, []);

  const scrollToWebcam = () => {
    const webcamSection = document.getElementById('webcam-section');
    if (webcamSection) {
      webcamSection.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <HeroContainer ref={heroRef}>
      <VideoBg autoPlay loop muted playsInline>
        <source src="/your-hero-video.mp4" type="video/mp4" />
        {/* Add fallback image or message */}
      </VideoBg>
      <Overlay />
      <Content>
        <Title>Posture Perfect</Title>
        <Subtitle>AI-Powered Posture Analysis for Better Health</Subtitle>
        <CallToAction onClick={scrollToWebcam}>Get Started</CallToAction>
      </Content>
    </HeroContainer>
  );
};

export default AnimatedHero;
