import React, { useEffect } from 'react';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './AnimatedHero.css';

gsap.registerPlugin(ScrollTrigger);

const AnimatedHero = () => {
  useEffect(() => {
    gsap.to('.hero-title', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top top',
        end: 'bottom top',
        scrub: true,
        pin: true,
      },
      x: 0,
      opacity: 1,
      duration: 2,
    });

    gsap.to('.hero-background', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top top',
        end: 'bottom top',
        scrub: true,
      },
      backgroundColor: '#121212', // Darker color for premium look
      duration: 2,
    });

    gsap.to('.hero-subtitle', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top center',
        end: 'bottom top',
        scrub: true,
      },
      y: -50,
      opacity: 1,
      duration: 2,
    });
  }, []);

  return (
    <div className="hero-section">
      <div className="hero-background">
        <h1 className="hero-title">Welcome to Posture Detection</h1>
        <p className="hero-subtitle">Enhance your posture with AI-powered analysis</p>
      </div>
    </div>
  );
};

export default AnimatedHero;
