import React, { useEffect } from 'react';
import { motion } from 'framer-motion';
import { gsap } from 'gsap';
import { ScrollTrigger } from 'gsap/ScrollTrigger';
import './AnimatedHero.css';

gsap.registerPlugin(ScrollTrigger);

const AnimatedHero = () => {
  useEffect(() => {
    gsap.to('.hero-title', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top top',
        end: 'bottom top',
        scrub: true,
        pin: true,
      },
      x: 0,
      opacity: 1,
      duration: 2,
    });

    gsap.to('.hero-background', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top top',
        end: 'bottom top',
        scrub: true,
      },
      backgroundColor: '#a200caff', // Darker color for premium look
      duration: 2,
    });

    gsap.to('.hero-subtitle', {
      scrollTrigger: {
        trigger: '.hero-section',
        start: 'top center',
        end: 'bottom top',
        scrub: true,
      },
      y: -50,
      opacity: 1,
      duration: 2,
    });
  }, []);

  return (
    <motion.div
      className="hero-section"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 1.5 }}
    >
      <motion.div
        className="hero-background"
        initial={{ scale: 0.8 }}
        animate={{ scale: 1 }}
        transition={{ duration: 1.5 }}
      >
        <motion.h1
          className="hero-title"
          initial={{ x: -200, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ duration: 1.5 }}
        >
          Welcome to Posture Detection
        </motion.h1>
        <motion.p
          className="hero-subtitle"
          initial={{ y: 50, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 1.5 }}
        >
          Enhance your posture with AI-powered analysis
        </motion.p>
      </motion.div>
    </motion.div>
  );
};

export default AnimatedHero;
