import React, { useState, useEffect, useRef } from 'react';
import styled, { keyframes } from 'styled-components';

// Enhanced keyframe animations
const fadeInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const scoreAnimation = keyframes`
  from {
    stroke-dashoffset: 440;
  }
  to {
    stroke-dashoffset: calc(440 - (440 * var(--progress) / 100));
  }
`;

const pulse = keyframes`
  0%, 100% {
    transform: scale(1);
    box-shadow: 0 0 20px rgba(255, 215, 0, 0.3);
  }
  50% {
    transform: scale(1.05);
    box-shadow: 0 0 40px rgba(255, 215, 0, 0.6);
  }
`;

const shimmer = keyframes`
  0% {
    background-position: -1000px 0;
  }
  100% {
    background-position: 1000px 0;
  }
`;

const slideInLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const slideInRight = keyframes`
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const slideInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const chartBarGrow = keyframes`
  from {
    height: 0;
  }
  to {
    height: var(--height);
  }
`;

const AnalysisContainer = styled.div`
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.15), rgba(255, 255, 255, 0.05));
  border-radius: 25px;
  padding: 3rem;
  margin: 3rem 0;
  backdrop-filter: blur(20px);
  max-width: 900px;
  margin-left: auto;
  margin-right: auto;
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  animation: ${fadeInUp} 0.8s ease-out;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.1), transparent);
    animation: ${shimmer} 3s infinite;
  }
`;

const AnalysisHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;
  z-index: 2;
  
  h2 {
    font-size: 2.5rem;
    margin-bottom: 1.5rem;
    color: #FFD700;
    font-family: 'Poppins', sans-serif;
    font-weight: 700;
    text-shadow: 0 0 20px rgba(255, 215, 0, 0.5);
    animation: ${pulse} 3s ease-in-out infinite;
  }
  
  .analysis-type {
    background: linear-gradient(135deg, rgba(255, 215, 0, 0.3), rgba(255, 165, 0, 0.2));
    padding: 1rem 2rem;
    border-radius: 25px;
    display: inline-block;
    font-weight: 600;
    font-size: 1.1rem;
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 215, 0, 0.3);
    box-shadow: 0 10px 25px rgba(255, 215, 0, 0.2);
    transition: all 0.3s ease;
    
    &:hover {
      transform: translateY(-2px);
      box-shadow: 0 15px 35px rgba(255, 215, 0, 0.3);
    }
  }
`;

const ScoreContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 3rem 0;
  position: relative;
`;

const ScoreCircle = styled.div`
  width: 180px;
  height: 180px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  animation: ${fadeInUp} 1s ease-out 0.5s both;
`;

const CircularProgress = styled.svg`
  width: 100%;
  height: 100%;
  transform: rotate(-90deg);
  position: absolute;
  
  circle {
    fill: none;
    stroke-width: 8;
    stroke-linecap: round;
  }
  
  .background {
    stroke: rgba(255, 255, 255, 0.1);
  }
  
  .progress {
    stroke: ${props => {
      const score = props.score || 0;
      if (score >= 80) return '#4CAF50';
      if (score >= 60) return '#FFD700';
      return '#FF6B6B';
    }};
    stroke-dasharray: 440;
    stroke-dashoffset: calc(440 - (440 * ${props => props.score || 0} / 100));
    transition: stroke-dashoffset 2s ease-out, stroke 0.5s ease;
    filter: drop-shadow(0 0 10px ${props => {
      const score = props.score || 0;
      if (score >= 80) return 'rgba(76, 175, 80, 0.6)';
      if (score >= 60) return 'rgba(255, 215, 0, 0.6)';
      return 'rgba(255, 107, 107, 0.6)';
    }});
  }
`;

const ScoreText = styled.div`
  position: relative;
  z-index: 2;
  text-align: center;
  
  .score-value {
    font-size: 2.5rem;
    font-weight: 700;
    color: ${props => {
      const score = props.score || 0;
      if (score >= 80) return '#4CAF50';
      if (score >= 60) return '#FFD700';
      return '#FF6B6B';
    }};
    text-shadow: 0 0 15px ${props => {
      const score = props.score || 0;
      if (score >= 80) return 'rgba(76, 175, 80, 0.5)';
      if (score >= 60) return 'rgba(255, 215, 0, 0.5)';
      return 'rgba(255, 107, 107, 0.5)';
    }};
    animation: ${fadeInUp} 1s ease-out 1s both;
