function App() {
  const [mode, setMode] = useState('webcam'); // 'webcam' or 'upload'
  const [analysisType, setAnalysisType] = useState('squat'); // 'squat' or 'desk'
  const [analysisResults, setAnalysisResults] = useState(null);
  const [currentSection, setCurrentSection] = useState('hero');
  const [isNavScrolled, setIsNavScrolled] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Loading screen effect
    const timer = setTimeout(() => {
      setIsLoading(false);
    }, 2000);

    // Scroll detection for navigation
    const handleScroll = () => {
      setIsNavScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      clearTimeout(timer);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleAnalysisComplete = (results) => {
    setAnalysisResults(results);
  };

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
      setCurrentSection(sectionId);
    }
  };

  const analysisTypes = [
    {
      id: 'squat',
      icon: 'üèãÔ∏è‚Äç‚ôÇÔ∏è',
      title: 'Squat Analysis',
      description: 'Advanced AI analysis for perfect squat form with real-time feedback'
    },
    {
      id: 'desk',
      icon: 'üíª',
      title: 'Desk Posture',
      description: 'Monitor your workspace posture for better health and productivity'
    },
    {
      id: 'walking',
      icon: 'üö∂‚Äç‚ôÇÔ∏è',
      title: 'Walking Gait',
      description: 'Analyze your walking pattern and movement efficiency'
    },
    {
      id: 'standing',
      icon: 'üßç‚Äç‚ôÇÔ∏è',
      title: 'Standing Posture',
      description: 'Check your standing alignment and balance'
    }
  ];

  const modes = [
    {
      id: 'webcam',
      icon: 'üìπ',
      title: 'Live Analysis',
      description: 'Real-time posture analysis with instant feedback using your webcam'
    },
    {
      id: 'upload',
      icon: 'üìÅ',
      title: 'Video Upload',
      description: 'Upload your exercise videos for detailed analysis and recommendations'
    }
  ];

  if (isLoading) {
    return (
      <LoadingScreen>
        <LoadingContent>
          <LoadingSpinner />
          <LoadingText>Loading AlignIQ...</LoadingText>
          <LoadingSubtext>Preparing your posture analysis experience</LoadingSubtext>
        </LoadingContent>
      </LoadingScreen>
    );
  }

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <NavBar className={isNavScrolled ? 'scrolled' : ''}>
          <NavContent>
            <Logo>AlignIQ</Logo>
            <NavLinks>
              <NavLink onClick={() => scrollToSection('hero')}>Home</NavLink>
              <NavLink onClick={() => scrollToSection('analysis')}>Analysis</NavLink>
              <NavLink onClick={() => scrollToSection('about')}>About</NavLink>
              <NavLink onClick={() => scrollToSection('testimonials')}>Reviews</NavLink>
              <NavLink onClick={() => scrollToSection('faq')}>FAQ</NavLink>
              <NavLink onClick={() => scrollToSection('contact')}>Contact</NavLink>
            </NavLinks>
          </NavContent>
        </NavBar>

        <Section id="hero">
          <AnimatedHero onStartAnalysis={() => scrollToSection('analysis')} />
        </Section>

        <AnalysisSection id="analysis">
          <MainContent>
            <SectionTitle>üéØ Choose Your Analysis Type</SectionTitle>

            <AnalysisTypeSelector>
              {analysisTypes.map((type) => (
                <TypeButton
                  key={type.id}
                  active={analysisType === type.id}
                  onClick={() => setAnalysisType(type.id)}
                  title={type.description}
                >
                  {type.icon} {type.title}
                </TypeButton>
              ))}
            </AnalysisTypeSelector>

            <SectionTitle style={{ fontSize: '2rem', marginTop: '4rem' }}>
              üìä Select Analysis Method
            </SectionTitle>

            <ModeSelector>
              {modes.map((modeOption) => (
                <ModeCard
                  key={modeOption.id}
                  active={mode === modeOption.id}
                  onClick={() => setMode(modeOption.id)}
                >
                  <ModeIcon>{modeOption.icon}</ModeIcon>
                  <ModeTitle>{modeOption.title}</ModeTitle>
                  <ModeDescription>{modeOption.description}</ModeDescription>
                </ModeCard>
              ))}
            </ModeSelector>

            {mode === 'webcam' ? (
              <WebcamCapture 
                analysisType={analysisType}
                onAnalysisComplete={handleAnalysisComplete}
              />
            ) : (
              <VideoUpload 
                analysisType={analysisType}
                onAnalysisComplete={handleAnalysisComplete}
              />
            )}

            {analysisResults && (
              <PostureAnalysis results={analysisResults} />
            )}
          </MainContent>
        </AnalysisSection>

        <Section id="about">
          <AboutSection />
        </Section>

        <Section id="testimonials">
          <TestimonialsSection />
        </Section>

        <Section id="faq">
          <FAQSection />
        </Section>

        <Section id="contact">
          <ContactSection />
        </Section>
      </AppContainer>
    </>
  );
}

