import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// Advanced animations
const heroFloat = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  25% { transform: translateY(-20px) rotate(1deg); }
  50% { transform: translateY(-10px) rotate(0deg); }
  75% { transform: translateY(-30px) rotate(-1deg); }
`;

const gradientFlow = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const slideInLeft = keyframes`
  from {
    opacity: 0;
    transform: translateX(-100px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const slideInRight = keyframes`
  from {
    opacity: 0;
    transform: translateX(100px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
`;

const bounceIn = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.3);
  }
  50% {
    opacity: 1;
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    opacity: 1;
    transform: scale(1);
  }
`;

const HeroContainer = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(-45deg, #667eea, #764ba2, #f093fb, #f5576c);
  background-size: 400% 400%;
  animation: ${gradientFlow} 15s ease infinite;
  position: relative;
  overflow: hidden;
  padding: 2rem 0;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: url('data:image/svg+xml,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100"><defs><pattern id="hexagons" width="28" height="49" patternUnits="userSpaceOnUse" patternTransform="scale(0.5)"><polygon points="14,1 26,7 26,21 14,27 2,21 2,7" fill="none" stroke="rgba(255,255,255,0.05)" stroke-width="1"/></pattern></defs><rect width="100" height="100" fill="url(%23hexagons)"/></svg>');
  }
`;

const HeroContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;
  position: relative;
  z-index: 1;
  
  @media (max-width: 968px) {
    grid-template-columns: 1fr;
    gap: 3rem;
    text-align: center;
  }
`;

const TextContent = styled.div`
  animation: ${slideInLeft} 1s ease-out;
  
  @media (max-width: 968px) {
    order: 2;
  }
`;

const HeroTitle = styled.h1`
  font-size: 3.5rem;
  font-weight: 900;
  line-height: 1.2;
  margin-bottom: 1.5rem;
  background: linear-gradient(45deg, #ffffff, #f8f9fa, #e9ecef);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  
  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
  
  @media (max-width: 480px) {
    font-size: 2rem;
  }
`;

const HeroSubtitle = styled.p`
  font-size: 1.3rem;
  color: rgba(255, 255, 255, 0.9);
  line-height: 1.6;
  margin-bottom: 2rem;
  max-width: 500px;
  
  @media (max-width: 768px) {
    font-size: 1.1rem;
  }
`;

const HeroButtons = styled.div`
  display: flex;
  gap: 1.5rem;
  flex-wrap: wrap;
  
  @media (max-width: 968px) {
    justify-content: center;
  }
  
  @media (max-width: 480px) {
    flex-direction: column;
    align-items: center;
  }
`;

const PrimaryButton = styled.button`
  padding: 1.2rem 2.5rem;
  font-size: 1.1rem;
  font-weight: bold;
  border: none;
  border-radius: 50px;
  background: linear-gradient(45deg, #ffeaa7, #fdcb6e);
  color: #2d3436;
  cursor: pointer;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(255, 234, 167, 0.4);
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
    transition: left 0.5s;
  }
  
  &:hover {
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(255, 234, 167, 0.6);
  }
  
  &:hover::before {
    left: 100%;
  }
`;

const SecondaryButton = styled.button`
  padding: 1.2rem 2.5rem;
  font-size: 1.1rem;
  font-weight: bold;
  border: 2px solid rgba(255, 255, 255, 0.8);
  border-radius: 50px;
  background: transparent;
  color: white;
  cursor: pointer;
  backdrop-filter: blur(10px);
  transition: all 0.3s ease;
  
  &:hover {
    background: rgba(255, 255, 255, 0.1);
    transform: translateY(-3px);
    box-shadow: 0 8px 25px rgba(255, 255, 255, 0.2);
  }
`;

const VisualContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  animation: ${slideInRight} 1s ease-out;
  
  @media (max-width: 968px) {
    order: 1;
  }
`;

const HeroGraphic = styled.div`
  width: 400px;
  height: 400px;
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0.05));
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.3);
  animation: ${heroFloat} 6s ease-in-out infinite;
  position: relative;
  
  @media (max-width: 768px) {
    width: 300px;
    height: 300px;
  }
  
  @media (max-width: 480px) {
    width: 250px;
    height: 250px;
  }
  
  &::before {
    content: '';
    position: absolute;
    width: 120%;
    height: 120%;
    border: 2px solid rgba(255, 234, 167, 0.3);
    border-radius: 50%;
    animation: ${heroFloat} 8s ease-in-out infinite reverse;
  }
  
  &::after {
    content: '';
    position: absolute;
    width: 140%;
    height: 140%;
    border: 1px solid rgba(255, 255, 255, 0.1);
    border-radius: 50%;
    animation: ${heroFloat} 10s ease-in-out infinite;
  }
`;

const GraphicIcon = styled.div`
  font-size: 8rem;
  animation: ${bounceIn} 2s ease-out 0.5s both;
  
  @media (max-width: 768px) {
    font-size: 6rem;
  }
  
  @media (max-width: 480px) {
    font-size: 4rem;
  }
`;

const FeatureCards = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 4rem;
  grid-column: 1 / -1;
`;

const FeatureCard = styled.div`
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  border-radius: 20px;
  padding: 2rem;
  text-align: center;
  transition: all 0.3s ease;
  animation: ${bounceIn} 1s ease-out ${props => props.delay}s both;
  
  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
    border-color: rgba(255, 234, 167, 0.5);
  }
`;

const CardIcon = styled.div`
  font-size: 3rem;
  margin-bottom: 1rem;
`;

const CardTitle = styled.h3`
  font-size: 1.3rem;
  margin-bottom: 1rem;
  color: white;
`;

const CardDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

const AnimatedHero = ({ onStartAnalysis }) => {
  const [currentFeature, setCurrentFeature] = useState(0);

  const features = [
    {
      icon: '🤖',
      title: 'AI-Powered Analysis',
      description: 'Advanced machine learning algorithms analyze your posture with scientific precision'
    },
    {
      icon: '⚡',
      title: 'Real-Time Feedback',
      description: 'Get instant corrections and suggestions as you exercise or work'
    },
    {
      icon: '📊',
      title: 'Detailed Reports',
      description: 'Comprehensive analytics and progress tracking for continuous improvement'
    },
    {
      icon: '🎯',
      title: 'Personalized Training',
      description: 'Customized exercise plans based on your specific posture needs'
    }
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentFeature((prev) => (prev + 1) % features.length);
    }, 3000);

    return () => clearInterval(interval);
  }, [features.length]);

  const scrollToAnalysis = () => {
    const element = document.getElementById('analysis');
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
    if (onStartAnalysis) {
      onStartAnalysis();
    }
  };

  const watchDemo = () => {
    // Demo functionality - could open a modal or video
    alert('Demo video coming soon! Start your analysis below to see AlignIQ in action.');
  };

  return (
    <HeroContainer>
      <HeroContent>
        <TextContent>
          <HeroTitle>
            Perfect Your Posture with AI Precision
          </HeroTitle>
          <HeroSubtitle>
            Transform your health and performance with our revolutionary AI-powered posture analysis. 
            Get real-time feedback, prevent injuries, and unlock your body's full potential.
          </HeroSubtitle>
          <HeroButtons>
            <PrimaryButton onClick={scrollToAnalysis}>
              🚀 Start Analysis Now
            </PrimaryButton>
            <SecondaryButton onClick={watchDemo}>
              ▶️ Watch Demo
            </SecondaryButton>
          </HeroButtons>
        </TextContent>

        <VisualContent>
          <HeroGraphic>
            <GraphicIcon>🏃‍♂️</GraphicIcon>
          </HeroGraphic>
        </VisualContent>

        <FeatureCards>
          {features.map((feature, index) => (
            <FeatureCard key={index} delay={0.2 * index}>
              <CardIcon>{feature.icon}</CardIcon>
              <CardTitle>{feature.title}</CardTitle>
              <CardDescription>{feature.description}</CardDescription>
            </FeatureCard>
          ))}
        </FeatureCards>
      </HeroContent>
    </HeroContainer>
  );
};

export default AnimatedHero;
