import React, { useState } from 'react';
import styled from 'styled-components';
import WebcamCapture from './components/WebcamCapture';
import VideoUpload from './components/VideoUpload';
import PostureAnalysis from './components/PostureAnalysis';
import Header from './components/Header';
import AnimatedHero from './components/AnimatedHero';
import './App.css';

const AppContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  color: white;
`;

const MainContent = styled.div`
  padding: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const ModeSelector = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 2rem 0;
  flex-wrap: wrap;
`;

const ModeButton = styled.button`
  padding: 1rem 2rem;
  border: none;
  border-radius: 10px;
  background: ${props => props.active ? '#4CAF50' : 'rgba(255, 255, 255, 0.2)'};
  color: white;
  font-size: 1rem;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
    background: ${props => props.active ? '#45a049' : 'rgba(255, 255, 255, 0.3)'};
  }
`;

const AnalysisTypeSelector = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  margin: 1rem 0;
`;

const TypeButton = styled.button`
  padding: 0.8rem 1.5rem;
  border: 2px solid ${props => props.active ? '#FFD700' : 'rgba(255, 255, 255, 0.3)'};
  border-radius: 25px;
  background: ${props => props.active ? '#FFD700' : 'transparent'};
  color: ${props => props.active ? '#333' : 'white'};
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s ease;

  &:hover {
    border-color: #FFD700;
    transform: scale(1.05);
  }
`;

function App() {
  const [mode, setMode] = useState('webcam'); // 'webcam' or 'upload'
  const [analysisType, setAnalysisType] = useState('squat'); // 'squat' or 'desk'
  const [analysisResults, setAnalysisResults] = useState(null);

  const handleAnalysisComplete = (results) => {
    setAnalysisResults(results);
  };

  return (
    <AppContainer>
      <AnimatedHero />
      <Header />
      <MainContent>
        <AnalysisTypeSelector>
          <TypeButton 
            active={analysisType === 'squat'} 
            onClick={() => setAnalysisType('squat')}
          >
            🏋️ Squat Analysis
          </TypeButton>
          <TypeButton 
            active={analysisType === 'desk'} 
            onClick={() => setAnalysisType('desk')}
          >
            💺 Desk Posture
          </TypeButton>
        </AnalysisTypeSelector>

        <ModeSelector>
          <ModeButton 
            active={mode === 'webcam'} 
            onClick={() => setMode('webcam')}
          >
            📹 Live Webcam
          </ModeButton>
          <ModeButton 
            active={mode === 'upload'} 
            onClick={() => setMode('upload')}
          >
            📁 Upload Video
          </ModeButton>
        </ModeSelector>

        <div id="webcam-section">
          {mode === 'webcam' ? (
            <WebcamCapture 
              analysisType={analysisType}
              onAnalysisComplete={handleAnalysisComplete}
            />
          ) : (
            <VideoUpload 
              analysisType={analysisType}
              onAnalysisComplete={handleAnalysisComplete}
            />
          )}
        </div>

        {analysisResults && (
          <PostureAnalysis results={analysisResults} />
        )}
      </MainContent>
    </AppContainer>
  );
}

export default App;
