import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';
import gsap from 'gsap';
import { Typography, Card, CardContent, Grid } from '@mui/material';

const AnalysisContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  padding: 2rem;
  margin: 2rem 0;
  backdrop-filter: blur(10px);
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const PostureAnalysis = ({ results }) => {
  const analysisRef = useRef(null);

  useEffect(() => {
    gsap.fromTo(
      analysisRef.current,
      { opacity: 0, scale: 0.9 },
      { opacity: 1, scale: 1, duration: 1, ease: 'power3.out' }
    );
  }, []);

  if (!results) return null;

  const isVideoAnalysis = results.results && results.results.summary;
  const analysisData = isVideoAnalysis ? results.results : results;

  return (
    <AnalysisContainer ref={analysisRef}>
      <Typography variant="h4" gutterBottom style={{ color: '#FFD700', textAlign: 'center' }}>
        {isVideoAnalysis ? 'ðŸ“Š Analysis Results' : 'ðŸŽ¯ Real-time Analysis'}
      </Typography>

      <Grid container spacing={2} justifyContent="center">
        <Grid item>
          <Card style={{ background: 'rgba(255, 255, 255, 0.1)', color: '#FFD700' }}>
            <CardContent>
              <Typography variant="h6">Score</Typography>
              <Typography variant="h4" style={{ fontWeight: 'bold' }}>
                {analysisData.score || analysisData.summary?.averageScore}%
              </Typography>
            </CardContent>
          </Card>
        </Grid>
        <Grid item>
          <Card style={{ background: 'rgba(255, 255, 255, 0.1)', color: '#FFD700' }}>
            <CardContent>
              <Typography variant="h6">Issues</Typography>
              <Typography variant="h4" style={{ fontWeight: 'bold' }}>
                {analysisData.issues?.length || analysisData.summary?.totalIssues}
              </Typography>
            </CardContent>
          </Card>
        </Grid>
      </Grid>

      <Typography variant="body1" style={{ marginTop: '1rem', textAlign: 'center' }}>
        {analysisData.feedback?.join(' ') || 'Great posture! Keep it up!'}
      </Typography>
    </AnalysisContainer>
  );
};

export default PostureAnalysis;
