import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

// Enhanced animations
const slideInUp = keyframes`
  from {
    opacity: 0;
    transform: translateY(50px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const progressFill = keyframes`
  from { width: 0%; }
  to { width: var(--progress); }
`;

const pulse = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
`;

const AnalysisContainer = styled.div`
  background: linear-gradient(135deg, #2d3748 0%, #4a5568 100%);
  border-radius: 25px;
  padding: 3rem;
  margin: 3rem 0;
  backdrop-filter: blur(20px);
  max-width: 1000px;
  margin-left: auto;
  margin-right: auto;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.3);
  border: 1px solid rgba(255, 255, 255, 0.1);
  animation: ${slideInUp} 0.8s ease-out;
  position: relative;
  overflow: hidden;
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, rgba(255, 255, 255, 0.05) 0%, transparent 50%, rgba(255, 255, 255, 0.05) 100%);
    pointer-events: none;
  }
`;

const AnalysisHeader = styled.div`
  text-align: center;
  margin-bottom: 3rem;
  position: relative;
  z-index: 1;
  
  h2 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    background: linear-gradient(45deg, #ffeaa7, #fdcb6e, #e17055);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
  }
  
  .analysis-type {
    background: linear-gradient(45deg, #74b9ff, #0984e3);
    padding: 0.8rem 2rem;
    border-radius: 30px;
    display: inline-block;
    font-weight: bold;
    color: white;
    box-shadow: 0 4px 15px rgba(116, 185, 255, 0.3);
    margin-bottom: 1rem;
  }
  
  .timestamp {
    color: rgba(255, 255, 255, 0.7);
    font-size: 0.9rem;
  }
`;

const ScoreContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 3rem 0;
  position: relative;
`;

const ScoreCircle = styled.div`
  width: 180px;
  height: 180px;
  border-radius: 50%;
  background: conic-gradient(
    ${props => {
      const score = props.score || 0;
      if (score >= 80) return `#00b894 0deg, #00b894 ${score * 3.6}deg, rgba(255,255,255,0.1) ${score * 3.6}deg`;
      if (score >= 60) return `#fdcb6e 0deg, #fdcb6e ${score * 3.6}deg, rgba(255,255,255,0.1) ${score * 3.6}deg`;
      return `#e17055 0deg, #e17055 ${score * 3.6}deg, rgba(255,255,255,0.1) ${score * 3.6}deg`;
    }}
  );
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  animation: ${pulse} 2s ease-in-out infinite;
  
  &::before {
    content: '';
    position: absolute;
    width: 140px;
    height: 140px;
    background: linear-gradient(135deg, #2d3748, #4a5568);
    border-radius: 50%;
    backdrop-filter: blur(10px);
  }
  
  .score-content {
    position: relative;
    z-index: 1;
    text-align: center;
    
    .score-number {
      font-size: 2.5rem;
      font-weight: bold;
      color: white;
      margin-bottom: 0.5rem;
    }
    
    .score-label {
      font-size: 0.9rem;
      color: rgba(255, 255, 255, 0.7);
      text-transform: uppercase;
      letter-spacing: 1px;
    }
  }
`;

const ScoreIndicator = styled.div`
  position: absolute;
  top: -20px;
  right: -20px;
  width: 60px;
  height: 60px;
  border-radius: 50%;
  background: ${props => {
    const score = props.score || 0;
    if (score >= 80) return 'linear-gradient(45deg, #00b894, #00cec9)';
    if (score >= 60) return 'linear-gradient(45deg, #fdcb6e, #e17055)';
    return 'linear-gradient(45deg, #e17055, #d63031)';
  }};
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.3);
`;

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 2rem;
  margin: 3rem 0;
`;

const MetricCard = styled.div`
  background: linear-gradient(135deg, rgba(255, 255, 255, 0.1), rgba(255, 255, 255, 0.05));
  border-radius: 20px;
  padding: 2rem;
  text-align: center;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2);
    border-color: rgba(255, 234, 167, 0.3);
  }
  
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 4px;
    background: ${props => props.gradient || 'linear-gradient(45deg, #74b9ff, #0984e3)'};
  }
  
  .card-icon {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    filter: drop-shadow(0 2px 4px rgba(0, 0, 0, 0.3));
  }
  
  .card-title {
    font-weight: bold;
    margin-bottom: 1rem;
    color: #fff;
    font-size: 1.1rem;
  }
  
  .card-value {
    font-size: 2rem;
    font-weight: bold;
    background: ${props => props.gradient || 'linear-gradient(45deg, #74b9ff, #0984e3)'};
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }
`;

const ProgressSection = styled.div`
  margin: 3rem 0;
  
  h3 {
    margin-bottom: 2rem;
    color: #ffeaa7;
    font-size: 1.5rem;
    text-align: center;
  }
`;

const ProgressItem = styled.div`
  margin-bottom: 2rem;
  
  .progress-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-bottom: 0.5rem;
    
    .progress-label {
      font-weight: bold;
      color: white;
    }
    
    .progress-value {
      color: ${props => {
        const value = props.value || 0;
        if (value >= 80) return '#00b894';
        if (value >= 60) return '#fdcb6e';
        return '#e17055';
      }};
      font-weight: bold;
    }
  }
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 12px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 6px;
  overflow: hidden;
  position: relative;
  
  .progress-fill {
    height: 100%;
    background: ${props => {
      const value = props.value || 0;
      if (value >= 80) return 'linear-gradient(45deg, #00b894, #00cec9)';
      if (value >= 60) return 'linear-gradient(45deg, #fdcb6e, #f39c12)';
      return 'linear-gradient(45deg, #e17055, #d63031)';
    }};
    width: ${props => props.value || 0}%;
    border-radius: 6px;
    transition: width 1s ease-out;
    position: relative;
    
    &::after {
      content: '';
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.3), transparent);
      animation: ${progressFill} 2s ease-out;
    }
  }
`;

const FeedbackSection = styled.div`
  margin: 2rem 0;
  
  h3 {
    margin-bottom: 1rem;
    color: #FFD700;
  }
`;

const FeedbackList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
`;

const FeedbackItem = styled.div`
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 1rem;
  background: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  border-left: 4px solid ${props => props.isGood ? '#4CAF50' : '#ff6b6b'};
  
  .feedback-icon {
    font-size: 1.2rem;
    margin-top: 0.1rem;
  }
  
  .feedback-content {
    flex: 1;
    
    .feedback-type {
      font-weight: bold;
      margin-bottom: 0.25rem;
      color: ${props => props.isGood ? '#4CAF50' : '#ff6b6b'};
    }
    
    .feedback-text {
      opacity: 0.9;
      line-height: 1.4;
    }
  }
`;

const VideoSummary = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 10px;
  padding: 1.5rem;
  margin: 1.5rem 0;
  
  h4 {
    margin-bottom: 1rem;
    color: #FFD700;
  }
  
  .summary-stats {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
    gap: 1rem;
    margin-bottom: 1rem;
  }
  
  .stat-item {
    text-align: center;
    
    .stat-label {
      font-size: 0.9rem;
      opacity: 0.7;
      margin-bottom: 0.25rem;
    }
    
    .stat-value {
      font-size: 1.2rem;
      font-weight: bold;
      color: #FFD700;
    }
  }
`;

const IssueTypesList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const IssueTag = styled.span`
  background: rgba(255, 107, 107, 0.2);
  color: #ff6b6b;
  padding: 0.25rem 0.75rem;
  border-radius: 15px;
  font-size: 0.9rem;
  border: 1px solid rgba(255, 107, 107, 0.3);
`;

const PostureAnalysis = ({ results }) => {
  if (!results) return null;

  const isVideoAnalysis = results.results && results.results.summary;
  const analysisData = isVideoAnalysis ? results.results : results;
  
  const getScoreColor = (score) => {
    if (score >= 80) return '#4CAF50';
    if (score >= 60) return '#FFD700';
    return '#ff6b6b';
  };

  const getScoreRating = (score) => {
    if (score >= 80) return 'Excellent';
    if (score >= 60) return 'Good';
    if (score >= 40) return 'Fair';
    return 'Needs Improvement';
  };

  const formatIssueType = (issueType) => {
    return issueType.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  if (isVideoAnalysis) {
    const { summary, frames } = analysisData;
    
    return (
      <AnalysisContainer className="fade-in">
        <AnalysisHeader>
          <h2>üìä Analysis Results</h2>
          <div className="analysis-type">
            {results.analysisType === 'squat' ? 'üèãÔ∏è Squat Analysis' : 'üí∫ Desk Posture Analysis'}
          </div>
        </AnalysisHeader>

        <ScoreContainer>
          <ScoreCircle score={summary.averageScore}>
            <div className="score-text">{summary.averageScore}%</div>
          </ScoreCircle>
        </ScoreContainer>

        <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
          <h3 style={{ color: getScoreColor(summary.averageScore) }}>
            {getScoreRating(summary.averageScore)}
          </h3>
          <p style={{ opacity: 0.8 }}>Overall Rating: {summary.overallRating}</p>
        </div>

        <VideoSummary>
          <h4>üìπ Video Analysis Summary</h4>
          <div className="summary-stats">
            <div className="stat-item">
              <div className="stat-label">Frames Analyzed</div>
              <div className="stat-value">{summary.frameCount || frames.length}</div>
            </div>
            <div className="stat-item">
              <div className="stat-label">Total Issues</div>
              <div className="stat-value">{summary.totalIssues}</div>
            </div>
            <div className="stat-item">
              <div className="stat-label">Avg Score</div>
              <div className="stat-value">{summary.averageScore}%</div>
            </div>
          </div>
          
          {summary.issueTypes && summary.issueTypes.length > 0 && (
            <>
              <div style={{ marginTop: '1rem', marginBottom: '0.5rem', fontWeight: 'bold' }}>
                Common Issues Detected:
              </div>
              <IssueTypesList>
                {summary.issueTypes.map((issueType, index) => (
                  <IssueTag key={index}>
                    {formatIssueType(issueType)}
                  </IssueTag>
                ))}
              </IssueTypesList>
            </>
          )}
        </VideoSummary>

        <SummaryGrid>
          <SummaryCard>
            <div className="card-icon">üéØ</div>
            <div className="card-title">Accuracy</div>
            <div className="card-value">{summary.averageScore}%</div>
          </SummaryCard>
          <SummaryCard>
            <div className="card-icon">‚ö†Ô∏è</div>
            <div className="card-title">Issues Found</div>
            <div className="card-value">{summary.totalIssues}</div>
          </SummaryCard>
          <SummaryCard>
            <div className="card-icon">‚≠ê</div>
            <div className="card-title">Rating</div>
            <div className="card-value">{summary.overallRating}</div>
          </SummaryCard>
        </SummaryGrid>
      </AnalysisContainer>
    );
  }

  // Real-time analysis display
  const analysis = analysisData.analysis || analysisData;
  
  return (
    <AnalysisContainer className="fade-in">
      <AnalysisHeader>
        <h2>üéØ Real-time Analysis</h2>
        <div className="analysis-type">
          {analysis.postureType === 'squat' ? 'üèãÔ∏è Squat Analysis' : 'üí∫ Desk Posture Analysis'}
        </div>
      </AnalysisHeader>

      <ScoreContainer>
        <ScoreCircle score={analysis.score}>
          <div className="score-text">{analysis.score}%</div>
        </ScoreCircle>
      </ScoreContainer>

      <div style={{ textAlign: 'center', marginBottom: '2rem' }}>
        <h3 style={{ color: getScoreColor(analysis.score) }}>
          {getScoreRating(analysis.score)}
        </h3>
      </div>

      <FeedbackSection>
        <h3>üí° Feedback & Recommendations</h3>
        <FeedbackList>
          {analysis.issues && analysis.issues.length > 0 ? (
            analysis.feedback.map((feedback, index) => (
              <FeedbackItem key={index} isGood={false}>
                <div className="feedback-icon">‚ö†Ô∏è</div>
                <div className="feedback-content">
                  <div className="feedback-type">Issue Detected</div>
                  <div className="feedback-text">{feedback}</div>
                </div>
              </FeedbackItem>
            ))
          ) : (
            <FeedbackItem isGood={true}>
              <div className="feedback-icon">‚úÖ</div>
              <div className="feedback-content">
                <div className="feedback-type">Excellent Posture!</div>
                <div className="feedback-text">
                  Your posture looks great! Keep maintaining this good form.
                </div>
              </div>
            </FeedbackItem>
          )}
        </FeedbackList>
      </FeedbackSection>
    </AnalysisContainer>
  );
};

export default PostureAnalysis;
