import React, { useState, useCallback } from 'react';
import styled from 'styled-components';
import { useDropzone } from 'react-dropzone';
import axios from 'axios';
import config from '../config/config';

const UploadContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 2rem;
  margin: 2rem 0;
`;

const DropzoneContainer = styled.div`
  border: 3px dashed ${props => props.isDragActive ? '#FFD700' : 'rgba(255, 255, 255, 0.3)'};
  border-radius: 15px;
  padding: 3rem;
  text-align: center;
  cursor: pointer;
  transition: all 0.3s ease;
  background: ${props => props.isDragActive ? 'rgba(255, 215, 0, 0.1)' : 'rgba(255, 255, 255, 0.05)'};
  backdrop-filter: blur(10px);
  min-width: 400px;
  min-height: 200px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  
  &:hover {
    border-color: #FFD700;
    background: rgba(255, 215, 0, 0.1);
    transform: translateY(-2px);
  }
  
  @media (max-width: 768px) {
    min-width: 300px;
    padding: 2rem;
  }
`;

const UploadIcon = styled.div`
  font-size: 4rem;
  margin-bottom: 1rem;
  opacity: 0.7;
`;

const UploadText = styled.div`
  font-size: 1.2rem;
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const UploadSubtext = styled.div`
  font-size: 0.9rem;
  opacity: 0.7;
`;

const SelectedFile = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  padding: 1rem;
  display: flex;
  align-items: center;
  gap: 1rem;
  margin: 1rem 0;
  
  .file-info {
    flex: 1;
    text-align: left;
  }
  
  .file-name {
    font-weight: bold;
    margin-bottom: 0.25rem;
  }
  
  .file-size {
    font-size: 0.9rem;
    opacity: 0.7;
  }
`;

const AnalyzeButton = styled.button`
  padding: 1rem 2rem;
  border: none;
  border-radius: 10px;
  background: #4CAF50;
  color: white;
  font-size: 1.1rem;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s ease;
  
  &:hover {
    background: #45a049;
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }
  
  &:disabled {
    background: #666;
    cursor: not-allowed;
    transform: none;
  }
`;

const ProgressContainer = styled.div`
  width: 100%;
  max-width: 500px;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  padding: 1.5rem;
  text-align: center;
`;

const ProgressBar = styled.div`
  width: 100%;
  height: 8px;
  background: rgba(255, 255, 255, 0.2);
  border-radius: 4px;
  overflow: hidden;
  margin: 1rem 0;
  
  .progress-fill {
    height: 100%;
    background: linear-gradient(90deg, #4CAF50, #45a049);
    border-radius: 4px;
    transition: width 0.3s ease;
    width: ${props => props.progress}%;
  }
`;

const ProgressText = styled.div`
  font-weight: bold;
  margin-bottom: 0.5rem;
`;

const VideoUpload = ({ analysisType, onAnalysisComplete }) => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [uploadStatus, setUploadStatus] = useState('');

  const onDrop = useCallback((acceptedFiles) => {
    if (acceptedFiles.length > 0) {
      setSelectedFile(acceptedFiles[0]);
      setUploadProgress(0);
      setUploadStatus('');
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'video/*': ['.mp4', '.avi', '.mov', '.wmv', '.flv', '.webm']
    },
    maxFiles: 1,
    maxSize: 100 * 1024 * 1024 // 100MB
  });

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const analyzeVideo = async () => {
    if (!selectedFile) return;

    setIsUploading(true);
    setUploadProgress(0);
    setUploadStatus('Uploading video...');

    const formData = new FormData();
    formData.append('video', selectedFile);
    formData.append('analysisType', analysisType);

    try {
      const response = await axios.post('http://localhost:5000/api/upload-video', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
        onUploadProgress: (progressEvent) => {
          const progress = Math.round(
            (progressEvent.loaded * 100) / progressEvent.total
          );
          setUploadProgress(progress);
          
          if (progress === 100) {
            setUploadStatus('Processing video...');
          }
        },
      });

      setUploadStatus('Analysis complete!');
      onAnalysisComplete(response.data);
      
    } catch (error) {
      console.error('Error uploading video:', error);
      setUploadStatus('Error analyzing video. Please try again.');
      
      setTimeout(() => {
        setUploadStatus('');
      }, 3000);
    } finally {
      setIsUploading(false);
      setTimeout(() => {
        setUploadProgress(0);
      }, 2000);
    }
  };

  const removeFile = () => {
    setSelectedFile(null);
    setUploadProgress(0);
    setUploadStatus('');
  };

  return (
    <UploadContainer className="fade-in">
      {!selectedFile ? (
        <DropzoneContainer {...getRootProps()} isDragActive={isDragActive}>
          <input {...getInputProps()} />
          <UploadIcon>üé•</UploadIcon>
          <UploadText>
            {isDragActive
              ? 'Drop your video here...'
              : 'Drag & drop a video file here'}
          </UploadText>
          <UploadSubtext>
            or click to browse (MP4, AVI, MOV, etc. - Max 100MB)
          </UploadSubtext>
        </DropzoneContainer>
      ) : (
        <>
          <SelectedFile>
            <div style={{ fontSize: '2rem' }}>üé¨</div>
            <div className="file-info">
              <div className="file-name">{selectedFile.name}</div>
              <div className="file-size">{formatFileSize(selectedFile.size)}</div>
            </div>
            <button
              onClick={removeFile}
              style={{
                background: 'none',
                border: 'none',
                color: '#ff6b6b',
                fontSize: '1.5rem',
                cursor: 'pointer'
              }}
            >
              ‚ùå
            </button>
          </SelectedFile>

          <AnalyzeButton
            onClick={analyzeVideo}
            disabled={isUploading}
          >
            {isUploading ? 'üîÑ Analyzing...' : `üîç Analyze ${analysisType === 'squat' ? 'Squat' : 'Desk Posture'}`}
          </AnalyzeButton>
        </>
      )}

      {isUploading && (
        <ProgressContainer>
          <ProgressText>{uploadStatus}</ProgressText>
          <ProgressBar progress={uploadProgress}>
            <div className="progress-fill"></div>
          </ProgressBar>
          <div>{uploadProgress}%</div>
        </ProgressContainer>
      )}
    </UploadContainer>
  );
};

export default VideoUpload;
