import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import Webcam from 'react-webcam';
import io from 'socket.io-client';
import config from '../config/config';
import { motion } from 'framer-motion';
import { FaPlay, FaStop, FaCheckCircle, FaExclamationCircle } from 'react-icons/fa';

const WebcamContainer = styled(motion.div)`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1.5rem;
  margin: 2rem 0;
`;

const VideoContainer = styled(motion.div)`
  position: relative;
  border-radius: 15px;
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
`;

const WebcamElement = styled(Webcam)`
  width: 640px;
  height: 480px;
  object-fit: cover;
  
  @media (max-width: 768px) {
    width: 100%;
    max-width: 480px;
    height: auto;
  }
`;

const ControlsContainer = styled.div`
  display: flex;
  gap: 1rem;
  flex-wrap: wrap;
  justify-content: center;
`;

const ControlButton = styled.button`
  padding: 1rem 2rem;
  border: none;
  border-radius: 10px;
  background: ${props => {
    if (props.variant === 'start') return '#4CAF50';
    if (props.variant === 'stop') return '#f44336';
    return 'rgba(255, 255, 255, 0.2)';
  }};
  color: white;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
  position: relative;
  
  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 5px 15px rgba(0, 0, 0, 0.2);
  }
  
  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
    transform: none;
  }
`;

const Tooltip = styled.div`
  position: absolute;
  bottom: -1.5rem;
  background: rgba(0, 0, 0, 0.7);
  color: white;
  padding: 0.5rem;
  border-radius: 5px;
  font-size: 0.8rem;
  visibility: hidden;
  opacity: 0;
  transition: opacity 0.3s;

  ${ControlButton}:hover & {
    visibility: visible;
    opacity: 1;
  }
`;

const StatusContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding: 1.5rem;
  background: rgba(255, 255, 255, 0.1);
  border-radius: 15px;
  backdrop-filter: blur(10px);
  min-width: 300px;
`;

const StatusIndicator = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  font-size: 1.1rem;
  
  .status-dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: ${props => {
      if (props.status === 'analyzing') return '#FFD700';
      if (props.status === 'connected') return '#4CAF50';
      return '#f44336';
    }};
    animation: ${props => props.status === 'analyzing' ? 'pulse 1.5s infinite' : 'none'};
  }
`;

const FeedbackContainer = styled.div`
  background: rgba(255, 255, 255, 0.1);
  border-radius: 10px;
  padding: 1rem;
  margin-top: 1rem;
  text-align: left;
  max-width: 600px;
`;

const FeedbackItem = styled.div`
  padding: 0.5rem 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  
  &:last-child {
    border-bottom: none;
  }
  
  .feedback-type {
    font-weight: bold;
    color: ${props => props.isGood ? '#4CAF50' : '#FF6B6B'};
  }
  
  .feedback-text {
    margin-top: 0.25rem;
    opacity: 0.9;
  }
`;

const WebcamCapture = ({ analysisType, onAnalysisComplete }) => {
  const webcamRef = useRef(null);
  const socketRef = useRef(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isConnected, setIsConnected] = useState(false);
  const [currentFeedback, setCurrentFeedback] = useState(null);
  const [analysisInterval, setAnalysisInterval] = useState(null);
  useEffect(() => {
    // Initialize socket connection
    socketRef.current = io(config.SOCKET_URL);
    
    socketRef.current.on('connect', () => {
      setIsConnected(true);
      console.log('Connected to server');
    });

    socketRef.current.on('disconnect', () => {
      setIsConnected(false);
      console.log('Disconnected from server');
    });

    socketRef.current.on('analysis-result', (data) => {
      if (data.result && data.result.analysis) {
        setCurrentFeedback(data.result.analysis);
        onAnalysisComplete(data.result);
      }
    });

    socketRef.current.on('analysis-error', (error) => {
      console.error('Analysis error:', error);
    });

    return () => {
      if (socketRef.current) {
        socketRef.current.disconnect();
      }
      if (analysisInterval) {
        clearInterval(analysisInterval);
      }
    };
  }, [onAnalysisComplete]);

  const captureFrame = () => {
    if (webcamRef.current && isConnected) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        socketRef.current.emit('analyze-frame', {
          imageData: imageSrc,
          analysisType: analysisType
        });
      }
    }
  };

  const startAnalysis = () => {
    if (!isConnected) {
      alert('Not connected to server. Please wait and try again.');
      return;
    }

    setIsAnalyzing(true);
    setCurrentFeedback(null);

    // Capture frame immediately
    captureFrame();

    // Set up interval for continuous analysis
    const interval = setInterval(captureFrame, config.ANALYSIS_INTERVAL);
    setAnalysisInterval(interval);
  };

  const stopAnalysis = () => {
    setIsAnalyzing(false);
    if (analysisInterval) {
      clearInterval(analysisInterval);
      setAnalysisInterval(null);
    }
  };

  const getStatusText = () => {
    if (!isConnected) return 'Connecting to server...';
    if (isAnalyzing) return 'Analyzing posture...';
    return 'Ready to analyze';
  };

  const getStatusType = () => {
    if (!isConnected) return 'disconnected';
    if (isAnalyzing) return 'analyzing';
    return 'connected';
  };

  return (
    <WebcamContainer
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <VideoContainer
        initial={{ scale: 0.9 }}
        animate={{ scale: 1 }}
        transition={{ duration: 0.5 }}
      >
        <WebcamElement
          ref={webcamRef}
          screenshotFormat="image/jpeg"
          screenshotQuality={0.8}
          mirrored={true}
        />
      </VideoContainer>

      <ControlsContainer>
        <ControlButton
          variant="start"
          onClick={startAnalysis}
          disabled={!isConnected || isAnalyzing}
        >
          {isAnalyzing ? <FaExclamationCircle /> : <FaPlay />} Start Analysis
          <Tooltip>Start analyzing your posture</Tooltip>
        </ControlButton>

        <ControlButton
          variant="stop"
          onClick={stopAnalysis}
          disabled={!isAnalyzing}
        >
          <FaStop /> Stop Analysis
          <Tooltip>Stop the current analysis</Tooltip>
        </ControlButton>
      </ControlsContainer>

      <StatusContainer>
        <StatusIndicator status={getStatusType()}>
          <div className="status-dot"></div>
          {getStatusText()}
        </StatusIndicator>

        {currentFeedback && (
          <FeedbackContainer>
            <h4>🎯 Real-time Feedback:</h4>
            <div style={{ marginTop: '0.5rem' }}>
              <strong>Score: {currentFeedback.score}/100</strong>
            </div>
            {currentFeedback.feedback && currentFeedback.feedback.length > 0 ? (
              currentFeedback.feedback.map((feedback, index) => (
                <FeedbackItem key={index} isGood={false}>
                  <div className="feedback-type">
                    <FaExclamationCircle /> Issue Detected
                  </div>
                  <div className="feedback-text">{feedback}</div>
                </FeedbackItem>
              ))
            ) : (
              <FeedbackItem isGood={true}>
                <div className="feedback-type">
                  <FaCheckCircle /> Good Posture
                </div>
                <div className="feedback-text">Your posture looks great!</div>
              </FeedbackItem>
            )}
          </FeedbackContainer>
        )}
      </StatusContainer>
    </WebcamContainer>
  );
};

export default WebcamCapture;
